'use client';

import type { BlogPostPreviewComponentWithMetadatas, BlogTagId } from '@/types/Blog';
import type { MaybeNull } from '@rtm/shared-types/CustomUtilityTypes';
import type { BlogTag } from '##/config/contentlayer/blog/blogTags';
import type { ReactElementKey } from '@rtm/shared-types/React';
import type { Limit, Id } from '@rtm/shared-types/Numbers';
import type { FunctionComponent } from 'react';

import {
  doGetMaybeFilteredPostsCollection,
  doComputePaginatedElements,
  getSortedPostsCollection,
  shouldShowBottomToolbar,
  shouldShowTopToolbar
} from '@/components/ui/helpers/PaginatedElements/functions/client';
import { computeReconciliatedPageIndex, getSanitizedCurrentPage } from '@/components/ui/helpers/PaginatedElements/functions/pagination';
import { getPaginatedElementsCurrentSlice } from '@/components/ui/helpers/PaginatedElements/functions/paginatedElements';
import { PAGE_KEY } from '@/components/ui/helpers/PaginatedElements/constants';
import BlogConfigClient, { MAX_FILTER_INDEX } from '@/config/Blog/client';
import { useCallback, useEffect, useState, useMemo, useRef } from 'react';
import PaginatedElements from '@/components/ui/PaginatedElements';
import { createURLSearchParams } from '@rtm/shared-lib/html';
import usePagination from '@/components/hooks/usePagination';
import { useSearchParams, useRouter } from 'next/navigation';
import { packIds } from '@rtm/shared-lib/misc';

import { getSanitizedCurrentFilterIndex } from '../helpers/functions/filtersSelectWidget';
import { getUnpackedAndSanitizedFilters } from '../helpers/functions/filters';
import SubcategoryRelatedBlogPostsClientToolbar from './Toolbar';
import { FILTERS_KEY, TAGS_KEY } from '../helpers/constants';
import { ETagsSwitch } from './helpers/enums';

interface SubcategoryRelatedBlogPostsClientProps {
  postsCollection: BlogPostPreviewComponentWithMetadatas[];
  expectedTagsIds: Set<BlogTagId>;
  elementsPerPage: Limit;
  tags: BlogTag[];
  title: string;
}

const SubcategoryRelatedBlogPostsClient: FunctionComponent<SubcategoryRelatedBlogPostsClientProps> = ({
  postsCollection,
  elementsPerPage,
  expectedTagsIds,
  title,
  tags
}) => {
  const router = useRouter();
  const searchParams = useSearchParams();

  const selectedFilter = useMemo(() => getSanitizedCurrentFilterIndex(searchParams, MAX_FILTER_INDEX, FILTERS_KEY), [searchParams]);

  const selectedTagsIds = useMemo(() => getUnpackedAndSanitizedFilters(searchParams, expectedTagsIds, TAGS_KEY), [searchParams, expectedTagsIds]);

  const [selectedFilterSwitch, setSelectedFilterSwitch] = useState<boolean>(false);
  const [selectedTagSwitch, setSelectedTagSwitch] = useState<ETagsSwitch>(ETagsSwitch.OFF);

  const getMaybeFilteredPostsCollection = useCallback(
    () => doGetMaybeFilteredPostsCollection(selectedTagsIds, postsCollection),
    [postsCollection, selectedTagsIds]
  );

  const maybeFilteredPostsCollection = useMemo(() => getMaybeFilteredPostsCollection(), [getMaybeFilteredPostsCollection]);

  const computePaginatedElements = useCallback(
    () => doComputePaginatedElements(selectedFilter, maybeFilteredPostsCollection),
    [maybeFilteredPostsCollection, selectedFilter]
  );

  const paginatedElements = useMemo(() => computePaginatedElements(), [computePaginatedElements]);
  const pagesAmount = usePagination(paginatedElements, elementsPerPage);
  const currentPage = useMemo(() => getSanitizedCurrentPage(searchParams, pagesAmount, PAGE_KEY), [pagesAmount, searchParams]);

  const paginated = useMemo(
    () => <PaginatedElements paginatedElements={paginatedElements} elementsPerPage={elementsPerPage} currentPage={currentPage} />,
    [paginatedElements, elementsPerPage, currentPage]
  );

  const newSelectedFilter = useRef<MaybeNull<Id>>(null);
  const newSelectedTagsIds = useRef<MaybeNull<Id[]>>(null);
  const memorizedPageBeforeFiltering = useRef<MaybeNull<Id>>(null);

  useEffect(() => {
    if (!selectedFilterSwitch || newSelectedFilter.current === null) return;

    const slice = getPaginatedElementsCurrentSlice(currentPage, elementsPerPage, paginatedElements);

    const keys: ReactElementKey[] = [];
    for (const element of slice) if (element.key) keys.push(element.key);

    const toSorted = getSortedPostsCollection(newSelectedFilter.current, maybeFilteredPostsCollection);

    const newPageIndex = computeReconciliatedPageIndex(keys, toSorted, elementsPerPage);
    const q = createURLSearchParams({ [FILTERS_KEY]: newSelectedFilter.current, [PAGE_KEY]: newPageIndex }, searchParams);

    newSelectedFilter.current = null;

    router.replace(q, { scroll: false });
    setSelectedFilterSwitch(false);
  }, [selectedFilterSwitch, currentPage, elementsPerPage, maybeFilteredPostsCollection, paginatedElements, router, searchParams]);

  useEffect(() => {
    if (selectedTagSwitch === ETagsSwitch.OFF || newSelectedTagsIds.current === null) return;

    const slice = getPaginatedElementsCurrentSlice(currentPage, elementsPerPage, paginatedElements);

    const keys: ReactElementKey[] = [];
    for (const element of slice) if (element.key) keys.push(element.key);

    const toMaybeFiltered = doGetMaybeFilteredPostsCollection(newSelectedTagsIds.current, postsCollection);
    const toSorted = getSortedPostsCollection(selectedFilter, toMaybeFiltered);

    function handleSelecting(__newSelectedTagsIds: BlogTagId[]) {
      const newPageIndex = computeReconciliatedPageIndex(keys, toSorted, elementsPerPage);

      const packedTagsIds = packIds(__newSelectedTagsIds);
      const q = createURLSearchParams({ [TAGS_KEY]: packedTagsIds, [PAGE_KEY]: newPageIndex }, searchParams);

      newSelectedTagsIds.current = null;

      router.replace(q, { scroll: false });
    }

    function handleUnselecting(__newSelectedTagsIds: BlogTagId[]) {
      const newPaginatedElements = doComputePaginatedElements(selectedFilter, toMaybeFiltered);
      const newSlice = getPaginatedElementsCurrentSlice(currentPage, elementsPerPage, newPaginatedElements);

      const newKeys: ReactElementKey[] = [];
      for (const element of newSlice) if (element.key) newKeys.push(element.key);

      const firstCommonKey: MaybeNull<ReactElementKey> = keys.find((key) => newKeys.includes(key)) ?? null;
      if (firstCommonKey === null) {
        handleSelecting(__newSelectedTagsIds);
        return;
      }

      const packedTagsIds = packIds(__newSelectedTagsIds);
      const q = createURLSearchParams({ [TAGS_KEY]: packedTagsIds }, searchParams);

      newSelectedTagsIds.current = null;

      router.replace(q, { scroll: false });
    }

    function handleClearing(__newSelectedTagsIds: BlogTagId[]) {
      if (memorizedPageBeforeFiltering.current === null) {
        handleUnselecting(__newSelectedTagsIds);
        return;
      }

      const q = createURLSearchParams({ [PAGE_KEY]: memorizedPageBeforeFiltering.current, [TAGS_KEY]: null }, searchParams);

      router.replace(q, { scroll: false });
    }

    const handlers = {
      [ETagsSwitch.UNSELECTING]: handleUnselecting,
      [ETagsSwitch.SELECTING]: handleSelecting,
      [ETagsSwitch.CLEARING]: handleClearing
    } as const;

    handlers[selectedTagSwitch](newSelectedTagsIds.current);
    setSelectedTagSwitch(ETagsSwitch.OFF);
  }, [selectedTagSwitch, currentPage, elementsPerPage, paginatedElements, postsCollection, selectedFilter, selectedTagsIds, router, searchParams]);

  useEffect(() => {
    // eslint-disable-next-line no-magic-numbers
    if (selectedTagsIds.length !== 0) return;
    memorizedPageBeforeFiltering.current = getSanitizedCurrentPage(searchParams, pagesAmount, PAGE_KEY);
  }, [searchParams, selectedTagsIds, pagesAmount]);

  const showTopToolbar = shouldShowTopToolbar(postsCollection);
  const showBottomToolbar = shouldShowBottomToolbar(pagesAmount);

  return (
    <section className="w-full">
      <h1 className="mb-2 ltr:text-left rtl:text-right">{title}</h1>
      {showTopToolbar && (
        <SubcategoryRelatedBlogPostsClientToolbar
          filtersAssoc={BlogConfigClient.COMPARE_FUNCTIONS_USED_TO_SORT_POSTS_ON_BLOG_SUBCATEGORY_PAGE}
          memorizedPageBeforeFiltering={memorizedPageBeforeFiltering}
          setSelectedFilterSwitch={setSelectedFilterSwitch}
          setSelectedTagSwitch={setSelectedTagSwitch}
          newSelectedTagsIds={newSelectedTagsIds}
          postsAmount={paginatedElements.length}
          newSelectedFilter={newSelectedFilter}
          selectedTagsIds={selectedTagsIds}
          selectedFilter={selectedFilter}
          currentPage={currentPage}
          pagesAmount={pagesAmount}
          tags={tags}
        />
      )}
      {/* {ToDo} https://github.com/Tirraa/dashboard_rtm/issues/126 */}
      <div className="mb-4 flex min-w-full flex-col [&>article:not(:last-of-type)]:mb-6">{paginated}</div>
      {showBottomToolbar && (
        <SubcategoryRelatedBlogPostsClientToolbar
          filtersAssoc={BlogConfigClient.COMPARE_FUNCTIONS_USED_TO_SORT_POSTS_ON_BLOG_SUBCATEGORY_PAGE}
          memorizedPageBeforeFiltering={memorizedPageBeforeFiltering}
          setSelectedFilterSwitch={setSelectedFilterSwitch}
          setSelectedTagSwitch={setSelectedTagSwitch}
          newSelectedTagsIds={newSelectedTagsIds}
          postsAmount={paginatedElements.length}
          newSelectedFilter={newSelectedFilter}
          selectedTagsIds={selectedTagsIds}
          selectedFilter={selectedFilter}
          currentPage={currentPage}
          pagesAmount={pagesAmount}
          isBottomWidget
          tags={tags}
        />
      )}
    </section>
  );
};

export default SubcategoryRelatedBlogPostsClient;

import type { BlogPostPreviewComponentWithMetadatas, BlogCategoriesAndSubcategoriesAssoc, BlogSubcategoryPageProps, BlogTagId } from '@/types/Blog';
import type { BlogTag } from '##/config/contentlayer/blog/blogTags';
import type { FunctionComponent } from 'react';

import {
  getAllBlogPostsByCategoryAndSubcategoryAndLanguageUnstrict,
  isValidBlogCategoryAndSubcategoryPairInAnyLanguage,
  blogSubcategoryShouldTriggerNotFound
} from '@/lib/blog/api';
import BlogPostsNotFound from '@/components/ui/blog/BlogPostsNotFound';
import { indexedBlogTagOptions } from '##/lib/builders/unifiedImport';
import BlogPostPreview from '@/components/ui/blog/BlogPostPreview';
import BlogTaxonomy from '##/config/taxonomies/blog';
import I18nTaxonomy from '##/config/taxonomies/i18n';
import { getScopedI18n } from '@/i18n/server';
import BlogConfig from '@/config/Blog/server';
import { notFound } from 'next/navigation';
import { i18ns } from '##/config/i18n';

import SubcategoryRelatedBlogPostsClient from './Client';

const SubcategoryRelatedBlogPosts: FunctionComponent<BlogSubcategoryPageProps> = async ({ params }) => {
  const [category, subcategory, language] = [params[BlogTaxonomy.CATEGORY], params[BlogTaxonomy.SUBCATEGORY], params[I18nTaxonomy.LANGUAGE]];

  const isValidPair: boolean = await isValidBlogCategoryAndSubcategoryPairInAnyLanguage(category, subcategory);
  if (!isValidPair) notFound();

  const postsCollection: BlogPostPreviewComponentWithMetadatas[] = (
    await getAllBlogPostsByCategoryAndSubcategoryAndLanguageUnstrict(category, subcategory, language)
  ).map((post) => ({
    blogPostPreviewComp: <BlogPostPreview key={`${post._raw.flattenedPath}-paginated-blog-post`} language={language} post={post} />,
    tagsIndexes: post.tagsIndexes,
    language: post.language,
    title: post.title,
    tags: post.tags,
    date: post.date,
    _id: post._id
  }));

  if (blogSubcategoryShouldTriggerNotFound(postsCollection)) notFound();
  // eslint-disable-next-line no-magic-numbers
  else if (postsCollection.length === 0) return <BlogPostsNotFound />;

  const tags = Array.from(
    new Set<BlogTag>(postsCollection.reduce((accumulator, currentValue) => accumulator.concat(currentValue.tags), [] as BlogTag[]))
  );

  const scopedT = await getScopedI18n(i18ns.blogCategories);
  const narrowedCategoryAndSubcategoryAssoc = `${category}.${subcategory}` as BlogCategoriesAndSubcategoriesAssoc;
  const title = scopedT(`${narrowedCategoryAndSubcategoryAssoc}.title`);

  const expectedTagsIds = new Set<BlogTagId>(tags.map((tag) => indexedBlogTagOptions[tag]));

  return (
    <SubcategoryRelatedBlogPostsClient
      elementsPerPage={BlogConfig.DISPLAYED_BLOG_POSTS_ON_SUBCATEGORY_RELATED_PAGE_PAGINATION_LIMIT}
      postsCollection={postsCollection}
      expectedTagsIds={expectedTagsIds}
      title={title}
      tags={tags}
    />
  );
};

export default SubcategoryRelatedBlogPosts;

'use client';

import type { SubcategoryRelatedBlogPostsClientToolbarInnerProps } from '@/components/pages/blog/SubcategoryRelatedBlogPosts/ToolbarInner';
import type { MaybeNull } from '@rtm/shared-types/CustomUtilityTypes';
import type { Quantity } from '@rtm/shared-types/Numbers';
import type { FunctionComponent } from 'react';

import { useEffect, useState } from 'react';
import { cn } from '@/lib/tailwind';

import shouldShowPaginationWidget from './helpers/functions/shouldShowPaginationWidget';

interface SubcategoryRelatedBlogPostsClientToolbarProps extends SubcategoryRelatedBlogPostsClientToolbarInnerProps {}

function buildPlaceholder(isBottomWidget: boolean, pagesAmount: Quantity) {
  const margin = 'my-4';

  const bottomWidgetPlaceholder = <div className={cn(margin, 'min-h-[40px]')} />;
  const topWidgetTwoColsPlaceholder = <div className={cn(margin, 'min-h-[84px]')} />;
  const topWidgetOneColPlaceholder = <div className={cn(margin, 'min-h-[40px]')} />;

  if (isBottomWidget) return bottomWidgetPlaceholder;
  // eslint-disable-next-line no-magic-numbers
  if (shouldShowPaginationWidget(pagesAmount)) return topWidgetTwoColsPlaceholder;
  return topWidgetOneColPlaceholder;
}

// https://v3.gatsbyjs.com/docs/mdx/importing-and-using-components/#lazy-loading-components
const SubcategoryRelatedBlogPostsClientToolbar: FunctionComponent<SubcategoryRelatedBlogPostsClientToolbarProps> = (props) => {
  const { isBottomWidget, pagesAmount } = props;
  const placeholder = buildPlaceholder(Boolean(isBottomWidget), pagesAmount);

  const [Component, setComponent] = useState<MaybeNull<FunctionComponent<SubcategoryRelatedBlogPostsClientToolbarInnerProps>>>(null);
  useEffect(() => {
    // eslint-disable-next-line promise/catch-or-return
    import('@/components/pages/blog/SubcategoryRelatedBlogPosts/ToolbarInner').then((component) => setComponent(() => component.default));
  }, []);
  if (Component === null) return placeholder;
  return <Component {...props} />;
};
export default SubcategoryRelatedBlogPostsClientToolbar;

'use client';

import type { FunctionComponent, ReactElement } from 'react';
import type { Quantity } from '@rtm/shared-types/Numbers';

import { useSearchParams, usePathname } from 'next/navigation';
import { cn } from '@/lib/tailwind';

import type { FiltersSelectWidgetProps } from '../FiltersSelectWidget';
import type { TagsCommandWidgetProps } from '../TagsCommandWidget';
import type { PaginationWidgetProps } from '../PaginationWidget';

import { buildBottomRightWidgets, buildTopRightWidgets, buildTopLeftWidgets } from './helpers/functions/toolbarInner';

export interface SubcategoryRelatedBlogPostsClientToolbarInnerProps extends TagsCommandWidgetProps, PaginationWidgetProps, FiltersSelectWidgetProps {
  isBottomWidget?: boolean;
  postsAmount: Quantity;
}

const SubcategoryRelatedBlogPostsClientToolbarInner: FunctionComponent<SubcategoryRelatedBlogPostsClientToolbarInnerProps> = ({
  memorizedPageBeforeFiltering,
  setSelectedFilterSwitch,
  setSelectedTagSwitch,
  newSelectedTagsIds,
  newSelectedFilter,
  selectedTagsIds,
  isBottomWidget,
  selectedFilter,
  filtersAssoc,

  currentPage,
  pagesAmount,
  postsAmount,
  tags
}) => {
  const searchParams = useSearchParams();
  const pathname = usePathname();

  const leftWidgets: ReactElement[] = !isBottomWidget
    ? buildTopLeftWidgets({ memorizedPageBeforeFiltering, setSelectedTagSwitch, newSelectedTagsIds, selectedTagsIds, tags })
    : [];

  const rightWidgets = !isBottomWidget
    ? buildTopRightWidgets(
        { pagesAmount, postsAmount, currentPage },
        {
          setSelectedFilterSwitch,
          newSelectedFilter,
          selectedFilter,
          filtersAssoc
        }
      )
    : buildBottomRightWidgets(pathname, searchParams, { pagesAmount, currentPage });

  // eslint-disable-next-line no-magic-numbers
  if (rightWidgets.length + leftWidgets.length <= 0) return null;

  return (
    <nav
      className={cn('my-4 flex items-end justify-between', {
        // eslint-disable-next-line no-magic-numbers
        'justify-end': isBottomWidget || leftWidgets.length <= 0
      })}
    >
      {/* eslint-disable-next-line no-magic-numbers */}
      {leftWidgets.length > 0 && leftWidgets}
      {/* eslint-disable-next-line no-magic-numbers */}
      {rightWidgets.length > 0 && <div className="flex flex-col">{rightWidgets}</div>}
    </nav>
  );
};

export default SubcategoryRelatedBlogPostsClientToolbarInner;

import type { Quantity } from '@rtm/shared-types/Numbers';

// eslint-disable-next-line no-magic-numbers
const shouldShowPaginationWidget = (pagesAmount: Quantity): boolean => pagesAmount > 1;

export default shouldShowPaginationWidget;

import type { Quantity } from '@rtm/shared-types/Numbers';

// eslint-disable-next-line no-magic-numbers
const shouldShowTagsCommandWidget = (tagsAmount: Quantity): boolean => tagsAmount > 1;

export default shouldShowTagsCommandWidget;

import type { MaybeNull } from '@rtm/shared-types/CustomUtilityTypes';
import type { Quantity, Count, Id } from '@rtm/shared-types/Numbers';
import type { BlogTag } from '##/config/contentlayer/blog/blogTags';
import type { MutableRefObject, ReactElement } from 'react';
import type { FiltersAssoc } from '@/config/Blog/client';
import type { AppPath } from '@rtm/shared-types/Next';
import type { BlogTagId } from '@/types/Blog';

import { PAGE_KEY } from '@/components/ui/helpers/PaginatedElements/constants';

import type { ETagsSwitch } from '../enums';

import { buildDropdown } from '../../../helpers/functions/paginationWidget';
import shouldShowTagsCommandWidget from './shouldShowTagsCommandWidget';
import shouldShowPaginationWidget from './shouldShowPaginationWidget';
import FiltersSelectWidget from '../../../FiltersSelectWidget';
import TagsCommandWidget from '../../../TagsCommandWidget';
import PaginationWidget from '../../../PaginationWidget';

// eslint-disable-next-line no-magic-numbers
const shouldShowFiltersSelectWidget = (postsAmount: Quantity): boolean => postsAmount > 1;

const getMaybeDropdown = (
  showPaginationWidget: boolean,
  pagesAmount: Quantity,
  currentPage: Count,
  pathname: AppPath,
  searchParams: URLSearchParams,
  isBottomWidget?: boolean
): MaybeNull<ReactElement> =>
  !showPaginationWidget
    ? null
    : buildDropdown(pagesAmount, currentPage, pathname, searchParams, PAGE_KEY, { dropdownContentClassName: 'z-20', isBottomWidget });

function buildWidgetsForTopRight(
  paginationWidgetProps: {
    showPaginationWidget: boolean;
    pagesAmount: Quantity;
    currentPage: Count;
  },
  filtersWidgetProps: {
    newSelectedFilter: MutableRefObject<MaybeNull<Id>>;
    setSelectedFilterSwitch: (s: boolean) => unknown;
    showFiltersSelectWidget: boolean;
    filtersAssoc: FiltersAssoc;
    selectedFilter: Id;
  }
): ReactElement[] {
  const elements: ReactElement[] = [];

  if (filtersWidgetProps.showFiltersSelectWidget) {
    elements.push(
      <FiltersSelectWidget
        setSelectedFilterSwitch={filtersWidgetProps.setSelectedFilterSwitch}
        newSelectedFilter={filtersWidgetProps.newSelectedFilter}
        selectedFilter={filtersWidgetProps.selectedFilter}
        filtersAssoc={filtersWidgetProps.filtersAssoc}
        triggerClassName="z-20 mb-1 self-end"
        selectContentClassName="z-20"
        key="filters-widget"
      />
    );
  }

  if (paginationWidgetProps.showPaginationWidget) {
    elements.push(
      <PaginationWidget
        pagesAmount={paginationWidgetProps.pagesAmount}
        currentPage={paginationWidgetProps.currentPage}
        className="w-full justify-end"
        key="pagination-widget"
      />
    );
  }

  return elements;
}

function buildWidgetsForBottomRight(
  paginationWidgetProps: {
    showPaginationWidget: boolean;
    pagesAmount: Quantity;
    currentPage: Count;
  },
  pathname: AppPath,
  searchParams: URLSearchParams
): ReactElement[] {
  const elements: ReactElement[] = [];
  const isBottomWidget = true;

  const maybeDropdown = getMaybeDropdown(
    paginationWidgetProps.showPaginationWidget,
    paginationWidgetProps.pagesAmount,
    paginationWidgetProps.currentPage,
    pathname,
    searchParams,
    isBottomWidget
  );

  if (maybeDropdown !== null) elements.push(maybeDropdown);

  return elements;
}

export function buildTopLeftWidgets(tagsCommandWidgetProps: {
  memorizedPageBeforeFiltering: MutableRefObject<MaybeNull<Id>>;
  newSelectedTagsIds: MutableRefObject<MaybeNull<Id[]>>;
  setSelectedTagSwitch: (s: ETagsSwitch) => unknown;
  selectedTagsIds: BlogTagId[];
  tags: BlogTag[];
}): ReactElement[] {
  const showTagsCommandWidget = shouldShowTagsCommandWidget(tagsCommandWidgetProps.tags.length);

  const elements: ReactElement[] = [];

  if (showTagsCommandWidget) {
    elements.push(
      <TagsCommandWidget
        memorizedPageBeforeFiltering={tagsCommandWidgetProps.memorizedPageBeforeFiltering}
        setSelectedTagSwitch={tagsCommandWidgetProps.setSelectedTagSwitch}
        newSelectedTagsIds={tagsCommandWidgetProps.newSelectedTagsIds}
        selectedTagsIds={tagsCommandWidgetProps.selectedTagsIds}
        tags={tagsCommandWidgetProps.tags}
        key="tags-filter-widget"
      />
    );
  }

  return elements;
}

export function buildTopRightWidgets(
  paginationWidgetProps: {
    pagesAmount: Quantity;
    postsAmount: Quantity;
    currentPage: Count;
  },
  filtersWidgetProps: {
    newSelectedFilter: MutableRefObject<MaybeNull<Id>>;
    setSelectedFilterSwitch: (s: boolean) => unknown;
    filtersAssoc: FiltersAssoc;
    selectedFilter: Id;
  }
): ReactElement[] {
  const showPaginationWidget = shouldShowPaginationWidget(paginationWidgetProps.pagesAmount);
  const showFiltersSelectWidget = shouldShowFiltersSelectWidget(paginationWidgetProps.postsAmount);

  return buildWidgetsForTopRight(
    {
      pagesAmount: paginationWidgetProps.pagesAmount,
      currentPage: paginationWidgetProps.currentPage,
      showPaginationWidget
    },
    {
      setSelectedFilterSwitch: filtersWidgetProps.setSelectedFilterSwitch,
      newSelectedFilter: filtersWidgetProps.newSelectedFilter,
      selectedFilter: filtersWidgetProps.selectedFilter,
      filtersAssoc: filtersWidgetProps.filtersAssoc,
      showFiltersSelectWidget
    }
  );
}

export function buildBottomRightWidgets(
  pathname: AppPath,
  searchParams: URLSearchParams,
  paginationWidgetProps: {
    pagesAmount: Quantity;
    currentPage: Count;
  }
): ReactElement[] {
  const showPaginationWidget = shouldShowPaginationWidget(paginationWidgetProps.pagesAmount);

  return buildWidgetsForBottomRight(
    {
      pagesAmount: paginationWidgetProps.pagesAmount,
      currentPage: paginationWidgetProps.currentPage,
      showPaginationWidget
    },
    pathname,
    searchParams
  );
}

