import type { MaybeNull } from '@rtm/shared-types/CustomUtilityTypes';
import type { Id } from '@rtm/shared-types/Numbers';

import { sortNumbers, unpackIds } from '@rtm/shared-lib/misc';

/**
 * @throws {RangeError}
 */
function buildUnpackedAndSanitizedFilters(packedIds: string, expectedIds: Set<Id>) {
  const unpackedAndSanitizedFilters = sortNumbers(
    // eslint-disable-next-line no-magic-numbers
    Array.from(new Set<Id>(unpackIds(packedIds).filter((id) => expectedIds.has(id))))
  );

  return unpackedAndSanitizedFilters;
}

/**
 * @throws {RangeError}
 */
export function getUnpackedAndSanitizedFilters(searchParams: URLSearchParams, expectedIds: Set<Id>, filtersKey: string) {
  const packedIds: MaybeNull<string> = searchParams.get(filtersKey);
  if (packedIds === null) return [];

  const generatedUnpackedAndSanitizedFilters = buildUnpackedAndSanitizedFilters(packedIds, expectedIds);
  return generatedUnpackedAndSanitizedFilters;
}

import type { MaybeNull } from '@rtm/shared-types/CustomUtilityTypes';
import type { Limit } from '@rtm/shared-types/Numbers';

import { FIRST_FILTER_INDEX } from '../constants';

export function getSanitizedCurrentFilterIndex(searchParams: URLSearchParams, maxFilter: Limit, filtersKey: string) {
  const maybeUnsafeFilterFromUrl: MaybeNull<string> = searchParams.get(filtersKey);
  // Stryker Workaround 1. Pointless mutant
  // Stryker disable next-line ConditionalExpression
  const unsafeFilterFromUrl = maybeUnsafeFilterFromUrl === null ? FIRST_FILTER_INDEX : Number(maybeUnsafeFilterFromUrl);

  if (isNaN(unsafeFilterFromUrl)) return FIRST_FILTER_INDEX;
  // Stryker Workaround 2. Pointless mutant
  // Stryker disable next-line EqualityOperator
  if (unsafeFilterFromUrl < FIRST_FILTER_INDEX) return FIRST_FILTER_INDEX;
  // Stryker Workaround 3. Pointless mutant
  // Stryker disable next-line EqualityOperator
  if (unsafeFilterFromUrl > maxFilter) return maxFilter;
  return unsafeFilterFromUrl;
}

