/* v8 ignore start */
// Stryker disable all

export const PAGE_KEY = 'page';
export const FIRST_PAGE_PARAM = 1;

// Stryker restore all
/* v8 ignore stop */

import type { BlogPostPreviewComponentWithMetadatas, BlogTagId } from '@/types/Blog';
import type { Quantity, Index, Score } from '@rtm/shared-types/Numbers';
import type { FiltersAssoc } from '@/config/Blog/client';

import BlogConfigClient from '@/config/Blog/client';
import { Fragment } from 'react';

export function doGetMaybeFilteredPostsCollection(selectedTagsIds: BlogTagId[], postsCollection: BlogPostPreviewComponentWithMetadatas[]) {
  const maybeFilteredPostsCollection =
    // eslint-disable-next-line no-magic-numbers
    selectedTagsIds.length === 0
      ? postsCollection
      : postsCollection.filter((post) => post.tagsIndexes.some((tagId) => selectedTagsIds.includes(tagId)));

  return maybeFilteredPostsCollection;
}

export function getSortedPostsCollection(
  filterFunIndex: Index,
  maybeFilteredPostsCollection: BlogPostPreviewComponentWithMetadatas[],
  __filtersAssoc: FiltersAssoc = BlogConfigClient.COMPARE_FUNCTIONS_USED_TO_SORT_POSTS_ON_BLOG_SUBCATEGORY_PAGE
) {
  const getSortScore = (post1: BlogPostPreviewComponentWithMetadatas, post2: BlogPostPreviewComponentWithMetadatas): Score =>
    __filtersAssoc[filterFunIndex].score(post1, post2);

  const toSorted = maybeFilteredPostsCollection.toSorted((post1, post2) => getSortScore(post1, post2));
  return toSorted;
}

export function doComputePaginatedElements(
  filterFunIndex: Index,
  maybeFilteredPostsCollection: BlogPostPreviewComponentWithMetadatas[],
  __filtersAssoc: FiltersAssoc = BlogConfigClient.COMPARE_FUNCTIONS_USED_TO_SORT_POSTS_ON_BLOG_SUBCATEGORY_PAGE
) {
  const paginatedElements = getSortedPostsCollection(filterFunIndex, maybeFilteredPostsCollection, __filtersAssoc).map((post) => (
    <Fragment key={post._id}>{post.blogPostPreviewComp}</Fragment>
  ));

  return paginatedElements;
}

// eslint-disable-next-line no-magic-numbers
export const shouldShowTopToolbar = (postsCollection: BlogPostPreviewComponentWithMetadatas[]): boolean => postsCollection.length > 1;

// eslint-disable-next-line no-magic-numbers
export const shouldShowBottomToolbar = (pagesAmount: Quantity): boolean => pagesAmount > 1;

import type { Quantity, Count, Index } from '@rtm/shared-types/Numbers';
import type { Couple } from '@rtm/shared-types/CustomUtilityTypes';
import type { ReactElement } from 'react';

function getPaginatedElementsCurrentSliceStartAndEndIndexes(page: Count, elementsPerPage: Quantity): Couple<Index> {
  // eslint-disable-next-line no-magic-numbers
  const startIndex = Math.max(0, page - 1) * elementsPerPage;
  const endIndex = startIndex + elementsPerPage;

  return [startIndex, endIndex];
}

export function getPaginatedElementsCurrentSlice(page: Count, elementsPerPage: Quantity, paginatedElements: ReactElement[]): ReactElement[] {
  // Stryker Workaround 1. Pointless mutant, just a minor optimization.
  // Stryker disable next-line EqualityOperator,ConditionalExpression
  if (paginatedElements.length <= elementsPerPage) return paginatedElements;
  const [startIndex, endIndex] = getPaginatedElementsCurrentSliceStartAndEndIndexes(page, elementsPerPage);
  const currentSlice = paginatedElements.slice(startIndex, endIndex);

  return currentSlice;
}

import type { Quantity, Count, Index, Limit } from '@rtm/shared-types/Numbers';
import type { BlogPostPreviewComponentWithMetadatas } from '@/types/Blog';
import type { MaybeNull } from '@rtm/shared-types/CustomUtilityTypes';
import type { ReactElementKey } from '@rtm/shared-types/React';

import { FIRST_PAGE_PARAM } from '../constants';

export function getSanitizedCurrentPage(searchParams: URLSearchParams, maxPage: Limit, pageKey: string) {
  const maybeUnsafePageFromUrl: MaybeNull<string> = searchParams.get(pageKey);
  // Stryker Workaround 1. Pointless mutant
  // Stryker disable next-line ConditionalExpression
  const unsafePageFromUrl = maybeUnsafePageFromUrl === null ? FIRST_PAGE_PARAM : Number(maybeUnsafePageFromUrl);

  if (isNaN(unsafePageFromUrl)) return FIRST_PAGE_PARAM;
  // Stryker Workaround 2. Pointless mutant
  // Stryker disable next-line EqualityOperator
  if (unsafePageFromUrl < FIRST_PAGE_PARAM) return FIRST_PAGE_PARAM;
  // Stryker Workaround 3. Pointless mutant
  // Stryker disable next-line EqualityOperator
  if (unsafePageFromUrl > maxPage) return maxPage;
  return unsafePageFromUrl;
}

function findPageNumberByPaginatedElementIndex(paginatedElementIndex: Index, elementsPerPage: Quantity): Count {
  // eslint-disable-next-line no-magic-numbers
  const sanitizedIndex: Index = Math.max(0, paginatedElementIndex);

  // eslint-disable-next-line no-magic-numbers
  const retrievedPage: Count = Math.trunc(sanitizedIndex / elementsPerPage) + 1;
  return retrievedPage;
}

function findFirstCommonElementIndex(
  oldSliceIds: ReactElementKey[],
  maybeFilteredPostsCollection: BlogPostPreviewComponentWithMetadatas[]
  // eslint-disable-next-line no-magic-numbers
): Index | -1 {
  for (const postId of oldSliceIds) {
    // eslint-disable-next-line no-magic-numbers
    const maybeFirstCommonElementIndex: Index | -1 = maybeFilteredPostsCollection.findIndex((post) => post._id === postId);
    /* eslint-disable no-magic-numbers */
    // Stryker Workaround 4. Pointless mutant
    // Stryker disable next-line ConditionalExpression,UnaryOperator
    if (maybeFirstCommonElementIndex !== -1) return maybeFirstCommonElementIndex;
    /* eslint-enable no-magic-numbers */
  }
  /* eslint-disable no-magic-numbers */
  // Stryker Workaround 5. Pointless mutant
  // Stryker disable next-line UnaryOperator
  return -1;
  /* eslint-enable no-magic-numbers */
}

export function computeReconciliatedPageIndex(
  oldSliceIds: ReactElementKey[],
  maybeFilteredPostsCollection: BlogPostPreviewComponentWithMetadatas[],
  elementsPerPage: Quantity
  // eslint-disable-next-line no-magic-numbers
): Index {
  const commonElementIndex = findFirstCommonElementIndex(oldSliceIds, maybeFilteredPostsCollection);

  /* eslint-disable no-magic-numbers */
  // Stryker Workaround 6. Pointless mutant
  // Stryker disable next-line ConditionalExpression
  if (commonElementIndex === -1) return FIRST_PAGE_PARAM;
  /* eslint-enable no-magic-numbers */

  const newPage = findPageNumberByPaginatedElementIndex(commonElementIndex, elementsPerPage);
  return newPage;
}

