      await blogPostGuard({
        params: {
          [BlogTaxonomy.SUBCATEGORY]: TESTING_BLOG_FAKE_SUBCATEGORY,
          [I18nTaxonomy.LANGUAGE]: DEFAULT_LANGUAGE,
