    expect(
      buildBlogPostSlug({
        _raw: {
          sourceFilePath: '_' + BLOG_POSTS_FOLDER + `/category/subcategory/lang/${INDEX_TOKEN}` + EXT,
          flattenedPath: '_' + BLOG_POSTS_FOLDER + `/category/subcategory/lang`
        },
        _id: '_'
      })
    ).toBe(INDEX_TOKEN);
  });
});

