    const endChars: EmptyString = '';
    expect(endsWithChars('foo bar abc', endChars)).toBe(false);
    expect(endsWithChars('foo bar acb', endChars)).toBe(false);
    expect(endsWithChars('foo bar cba', endChars)).toBe(false);
