import type { DashboardRoutesKeys } from '@/config/DashboardSidebar/utils/RoutesMapping';
import type { FunctionComponent, ReactElement, ReactNode } from 'react';
import type { AppPath } from '@rtm/shared-types/Next';

import { computeHTMLElementHeight, computeHTMLElementWidth, getDirection } from '@rtm/shared-lib/html';
import DASHBOARD_ROUTES, { DASHBOARD_ROUTES_TITLES } from '@/config/DashboardSidebar/routesImpl';
import DASHBOARD_ROUTES_SIDEBAR_COMPONENTS from '@/config/DashboardSidebar/utils/IconsMapping';
import SidebarButtonStyle from '@/components/config/styles/sidebar/SidebarButtonStyle';
import getRefCurrentPtr from '@rtm/shared-lib/portable/react/getRefCurrentPtr';
import { getClientSideI18n, useCurrentLocale } from '@/i18n/client';
import { useMediaQuery } from '@react-hook/media-query';
import { useEffect, useState, useRef } from 'react';
import { getBreakpoint, cn } from '@/lib/tailwind';
import { hrefMatchesPathname } from '@/lib/str';
import { usePathname } from 'next/navigation';
import ROUTES_ROOTS from '##/config/routes';
import Link from 'next/link';

import DashboardSidebarCollapseButton from './DashboardSidebarCollapseButton';

interface DashboardSidebarProps {}

const { isNotActiveClassList, isActiveClassList } = SidebarButtonStyle;

function sidebarBtnsGenerator(currentPathname: AppPath): ReactNode[] {
  const keys = Object.keys(DASHBOARD_ROUTES_SIDEBAR_COMPONENTS);
  // eslint-disable-next-line @typescript-eslint/no-magic-numbers
  const lastKey = keys[keys.length - 1];
  const sidebarBtnsSeparator = <hr className="m-auto my-2 hidden w-5/6 lg:block" />;

  const globalT = getClientSideI18n();
  return keys.map((k): ReactElement => {
    const k2 = k as DashboardRoutesKeys;
    const [btnComponent, href, i18nPath] = [DASHBOARD_ROUTES_SIDEBAR_COMPONENTS[k2], DASHBOARD_ROUTES[k2], DASHBOARD_ROUTES_TITLES[k2]];
    const title = globalT(i18nPath);

    const sidebarButtonClassName = hrefMatchesPathname(href, currentPathname, ROUTES_ROOTS.DASHBOARD) ? isActiveClassList : isNotActiveClassList;

    return (
      <li key={`${k}-sidebar-btn-component`}>
        <Link className={cn('flex w-fit max-w-full flex-col rounded-lg', sidebarButtonClassName)} title={title} href={href}>
          <span className="sr-only">{title}</span>
          {btnComponent}
        </Link>
        {k !== lastKey && sidebarBtnsSeparator}
      </li>
    );
  });
}

const DashboardSidebar: FunctionComponent<DashboardSidebarProps> = () => {
  const wasCollapsed = useRef<boolean>(false);
  const [isCollapsed, setIsCollapsed] = useState<boolean>(wasCollapsed.current);
  const sidebarRef = useRef<HTMLDivElement>(null);
  const isLargeScreen = useMediaQuery(`(min-width: ${getBreakpoint('lg')}px)`);
  const currentLocale = useCurrentLocale();
  const currentPathname = usePathname();

  useEffect(() => {
    const sidebarInstance = getRefCurrentPtr(sidebarRef);
    if (!sidebarInstance) return;

    const EFFECT_CLASSES = ['transition-all', 'duration-300'];

    function applyUncollapsedStyles() {
      sidebarInstance.style.marginLeft = '0';
      sidebarInstance.style.marginRight = '0';
      sidebarInstance.style.marginTop = '0';
      wasCollapsed.current = false;
    }

    function applyCollapsedStyles() {
      const direction = getDirection();
      if (isLargeScreen) {
        sidebarInstance.style.marginTop = '0';
        if (direction === 'rtl') {
          sidebarInstance.style.marginRight = '-' + computeHTMLElementWidth(sidebarInstance) + 'px';
          sidebarInstance.style.marginLeft = '0';
        } else {
          sidebarInstance.style.marginLeft = '-' + computeHTMLElementWidth(sidebarInstance) + 'px';
          sidebarInstance.style.marginRight = '0';
        }
      } else {
        sidebarInstance.style.marginLeft = '0';
        sidebarInstance.style.marginTop = '-' + computeHTMLElementHeight(sidebarInstance) + 'px';
      }
      wasCollapsed.current = true;
    }

    if (!isCollapsed) {
      if (!wasCollapsed.current) sidebarInstance.classList.remove(...EFFECT_CLASSES);
      else sidebarInstance.classList.add(...EFFECT_CLASSES);

      applyUncollapsedStyles();
      return;
    }

    if (wasCollapsed.current) sidebarInstance.classList.remove(...EFFECT_CLASSES);
    else sidebarInstance.classList.add(...EFFECT_CLASSES);
    applyCollapsedStyles();
  }, [isCollapsed, sidebarRef, isLargeScreen, currentLocale]);

  return (
    <>
      <aside
        className={'z-20 w-full justify-center border-t-[1px] border-muted-foreground bg-black dark:bg-card lg:w-fit lg:border-0'}
        ref={sidebarRef}
      >
        <nav className="py-4 lg:overflow-y-auto lg:px-[22px]">
          <ul className={cn('flex flex-wrap justify-center gap-2 lg:block')} role="menu">
            {sidebarBtnsGenerator(currentPathname)}
          </ul>
        </nav>
      </aside>
      <DashboardSidebarCollapseButton setIsCollapsed={setIsCollapsed} isCollapsed={isCollapsed} />
    </>
  );
};

export default DashboardSidebar;

'use client';

import type { FunctionComponent } from 'react';

import SidebarCollapseButtonIconStyle, { SIZE_PX_VALUE } from '@/components/config/styles/sidebar/SidebarCollapseButtonIconStyle';
import SidebarCollapseButtonWrapperStyle from '@/components/config/styles/sidebar/SidebarCollapseButtonWrapperStyle';
import { ArrowSmallRightIcon, ArrowSmallDownIcon } from '@heroicons/react/20/solid';
import { useScopedI18n } from '@/i18n/client';
import { i18ns } from '##/config/i18n';
import { cn } from '@/lib/tailwind';

interface DashboardSidebarCollapseButtonProps {
  setIsCollapsed: Function;
  isCollapsed: boolean;
}

const { isNotActiveClassList: btnIconIsNotActiveClassList, isActiveClassList: btnIconIsActiveClassList } = SidebarCollapseButtonIconStyle;

const { isNotActiveClassList: btnWrapperIsNotActiveClassList, isActiveClassList: btnWrapperIsActiveClassList } = SidebarCollapseButtonWrapperStyle;

const DashboardSidebarCollapseButton: FunctionComponent<DashboardSidebarCollapseButtonProps> = ({ setIsCollapsed, isCollapsed }) => {
  const sidebarCollapseBtnIconClassList = isCollapsed ? btnIconIsActiveClassList : btnIconIsNotActiveClassList;
  const sidebarCollapseBtnWrapperClassList = !isCollapsed ? btnWrapperIsActiveClassList : btnWrapperIsNotActiveClassList;
  const sidebarCollapseBtnClassList =
    'relative bottom-1 m-auto h-8 w-12 rounded-full rounded-b-full rounded-t-none bg-black dark:bg-card lg:bottom-0 lg:right-2 lg:w-8 lg:rounded-l-none lg:rounded-r-full rtl:lg:-right-2 rtl:lg:rounded-l-full rtl:lg:rounded-r-none';

  const scopedT = useScopedI18n(`${i18ns.dashboard}.sr-only`);
  const ariaLabel = isCollapsed ? scopedT('show-sidebar') : scopedT('hide-sidebar');
  const ariaExpanded = isCollapsed ? 'false' : 'true';
  const type = 'button';

  return (
    <div className={sidebarCollapseBtnWrapperClassList} onClick={() => setIsCollapsed(!isCollapsed)}>
      <button className={cn(sidebarCollapseBtnClassList, 'hidden lg:inline')} aria-expanded={ariaExpanded} aria-label={ariaLabel} type={type}>
        <ArrowSmallRightIcon className={sidebarCollapseBtnIconClassList} height={SIZE_PX_VALUE} width={SIZE_PX_VALUE} />
      </button>

      <button className={cn(sidebarCollapseBtnClassList, 'lg:hidden')} aria-expanded={ariaExpanded} aria-label={ariaLabel} type={type}>
        <ArrowSmallDownIcon className={sidebarCollapseBtnIconClassList} height={SIZE_PX_VALUE} width={SIZE_PX_VALUE} />
      </button>
    </div>
  );
};

export default DashboardSidebarCollapseButton;

