  it('should pass (explicit default language narrowing, top root level path)', () => {
    const fake = _ as PageAdapter<{
      path: `${DefaultLanguage}/page-00`;
      url: `/${DefaultLanguage}/page-00`;
      pathWithoutHead: 'page-00';
      head: DefaultLanguage;
      nestingLevelTwo: '';
      tail: 'page-00';
    }>;

    expectType<{
      url: `/${DefaultLanguage}/page-00`;
