    }>;

    expectType<{
      url: `/${DefaultLanguage}/testing-pages-root/fake-nesting`;
      path: 'testing-pages-root/fake-nesting';
      root: 'testing-pages-root';
      lang: DefaultLanguage;
    }>(fake);
  });
