import buildPagePath from '../path';

describe('path', () => {
  const leaf = 'leaf';
  it('should return a valid path, given a valid input', () => {
    expect(
      buildPagePath({
        _raw: {
          flattenedPath: PAGES_FOLDER
        },
        _id: '_'
