  /* eslint-disable perfectionist/sort-objects */
  h1: ({ className, ...props }) => (
    <h1 className={cn('mt-2 flex scroll-m-20 text-4xl font-bold tracking-tight [&>*]:no-underline', className)} {...props} />
  ),
  h2: ({ className, ...props }) => (
