import { describe, expect, it } from 'vitest';

import surroundString from '../surroundString';

describe('surroundString', () => {
  it('should return envelope + foo + envelope', () => {
    const foo = 'foo';
    const envelope = 'bar';
    const expected = envelope + foo + envelope;

    expect(surroundString(foo, envelope)).toBe(expected);
    expect(surroundString(envelope + foo, envelope)).toBe(expected);
    expect(surroundString(foo + envelope, envelope)).toBe(expected);
    expect(surroundString(expected, envelope)).toBe(expected);
  });

  it("should return 'barbar', given '' as input and 'bar' as envelope", () => {
    const envelope = 'bar';
    expect(surroundString('', envelope)).toBe(envelope.repeat(2));
  });
});

import InvalidArgumentsError from '##/errors/InvalidArguments';
import { describe, expect, it } from 'vitest';

import countCharacter from '../countCharacter';

describe('countCharacter', () => {
  it('should return a positive value, given strings containing the searched char', () => {
    expect(countCharacter('aa    bbqsklqjdkqjdkqjd   aa   qlj,dqkdjqkdjkbb', 'a')).toBe(4);
    expect(countCharacter('aa    bbqsklqjdkqjdkqjd   aa   qlj,dqkdjqkdjkbb', '$')).toBe(0);
  });

  it('should throw, given char is not a char but a string', () => {
    expect(() => {
      countCharacter('aa    bbqsklqjdkqjdkqjd   aa   qlj,dqkdjqkdjkbb', 'asqdqd');
    }).toThrowError(InvalidArgumentsError);
  });
});

